package testes;

import dao.ImageDAO;
import dao.PlayerDAO;
import game.Images;
import game.Player;
import org.junit.Assert;
import org.junit.Test;

/**
 *
 * @author leonardo
 */
public class ImageTest {

//    @Test
    public void insertImageBd() {

        String img = "data:image/png;base64,";
        PlayerDAO playerDAO = new PlayerDAO();
        Player player = playerDAO.obter("lpbaiser");
        Images images = new Images(img, player);

        ImageDAO imageDAO = new ImageDAO();
        imageDAO.insert(images);

        Images images1 = imageDAO.obter(1);
        Assert.assertEquals(images.getImage(), images1.getImage());
    }
}
